import React from "react";
import styled from "styled-components";
import backgroundImage from "../assets/background2.png"

const UserSignUp = () =>{
    return (
        <div>         
          <Background/>
          <Wrapper>
            <FormWrapper>
              <h2 style={{"marginBottom": "10px","color":"grey"}}>Create your user account</h2>
              <Input type={"text"} 
              name="firstName" 
              placeholder="First Name" />
  
              <Input type={"text"} 
              name="lastName" 
              placeholder="Last Name" /> 

              <Input type={"text"}
              name="userName" 
              placeholder="User Name" />

              <Input type={"text"}
              name= "email"
              placeholder="email" />

              <Input type={"password"}
              name="password"
              placeholder="Password"/>


        <SubmitInput type="submit" value="Sign Up"/>
            </FormWrapper>
          </Wrapper>
        
        </div>

    );
  }

  const Wrapper = styled.div`
      /* display: flex;
      justify-content: center;
      align-items: center;
      text-align: center;
      min-height: 100vh; */
      position: absolute;
      top: 50%;
      left: 50%;
      margin-right: -50%;
      transform: translate(-50%, -50%);
      
    `
    
    const FormWrapper = styled.form`
    border-radius: 10px; 
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border: 4px solid #42c99d;
    box-shadow: 0px 0px 25px 1px rgb(40,176,255);
    background: #DDEDEC;
    width: 350px;
    height: 450px;
    `
    
    const Input= styled.input`
    border: none;
    background: #42c99d;
    font-size: 16px;
    height: 35px;
    margin: 0px 0px 20px 0px;
    padding:15px;
    border-radius: 10px; 
    `
    
  const SubmitInput = styled.input`
  color: white;
  font-weight: bold;
  padding: 12px 35px 12px 35px;
  margin-bottom: 10px;
  text-align: center;
  border: none;
  border-radius: 10px;
  background: #42c99d;
  /* &.active{
    color: white;
  } */
  &:hover{
    color: white;
    background-color: rgb(40,176,255);
    transition: 150ms;
  }
  `
    
    const Background = styled.div`
    background-image: url(${backgroundImage});
    position: fixed; 
    min-width: 100%;
    min-height: 100%;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    `




  export default UserSignUp;
