import React, {useState} from "react";
import styled, { ThemeConsumer } from "styled-components";
import backgroundImage from "/Users/oliverbloj/Documents/Concordia BootCamp/36.1 WM 5.5.2022/WorkFlow_Management_APP/client/src/assets/background2.png"

// we need to install npm cors 

const UserHomepage =() =>{
    // load data into useState
    const [uploadData, setUploadData]= useState()
    const filesUploadHandler= (e)=>{
        setUploadData(e.target.files[0])
        }

    const onSumbitHandler=(e) =>{
        e.preventDefault()
        // handle file data before sending it in the format that multer can read 
        const data = new FormData()
        data.append("files", uploadData)

        fetch("/uploads", {
            method: "POST",
            body: data,
        })
            .then((res)=>{
                console.log("Sucessful Upload")
                window.alert("Upload completed")
            })
            .catch((err)=>{
                console.log(err.message)
            })
    }    
    return(
        <>
            {/* <Background/> */}
            <h1>User Homepage</h1>
            <div>
                <FormWrapper onSubmit={onSumbitHandler}>
                    <input type={"file"} onChange={filesUploadHandler}/>
                    <button type="submit">Submit</button>
                </FormWrapper>
            </div> 
        </>
    )
}

// since we are using multer the encType needs to be mutipart/form-data

const Wrapper = styled.div`
      position: absolute;
      top: 50%;
      left: 50%;
      margin-right: -50%;
      transform: translate(-50%, -50%);
      background:red;

    `

const FormWrapper = styled.form`
    margin-top:80%;
    border-radius: 10px; 
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background: #DDEDEC;
    width: 100%;
    height: 100%;
    z-index: 1;
    `

const Background = styled.div`
background-image: url(${backgroundImage});
position: fixed; 
min-width: 100%;
min-height: 100%;
background-repeat: no-repeat;
background-size: cover;
background-position: center;
`


export default UserHomepage;
