import styled from "styled-components";
import backgroundImage from "/client/src/assets/background2.png"


const UserHomepage =() =>{
    return(
        <>
            <Background />        
            <h1>User Homepage</h1>
            <div>
                <form action="/uploads" method="POST" encType="mutipart/form-data">
                    <input type={"file"} name="files" uploads/>
                    <button type="submit">Submit</button>
                </form>
            </div> 
        </>
    )
}

// since we are using multer the encType needs to be mutipart/form-data

const Background = styled.div`
    background-image: url(${backgroundImage});
    position: fixed; 
    min-width: 100%;
    min-height: 100%;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    `

