import React from "react";
import styled from "styled-components";
//import { useNavigate } from "react-router-dom";
import {NavLink} from "react-router-dom";

import backgroundImage from "../assets/workflow-management.png"

const Homepage = () =>{

    return (
      <>
       <Background/> 

    <ListWrapper>
        <Box>

            <ListedItem>
              <NavigationLink to="/adminLogin"> Administrator Login </NavigationLink>
            </ListedItem>
            
            <ListedItem>
            <NavigationLink to="/userLogin"> User Login </NavigationLink>
            </ListedItem>

        </Box>
     </ListWrapper>
     
      </>
    );
}




const ListWrapper = styled.ul`
list-style-type: none;
position: absolute;
top: 50%;
left: 50%;
margin: -100px 0 0 -110px; /* Apply negative top and left margins to truly center the element */
`
const Box = styled.div`
background: #DDEDEC;
width: 250px;
height: 250px;
border-radius: 10px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
border: 2px solid #42c99d;
 box-shadow: 0px 0px 25px 1px grey; 
`

const ListedItem = styled.li`
/*  adds space between listed items instead of using margin-top */
line-height: 5em;
`

const NavigationLink = styled(NavLink)`
color: white;
font-weight: bold;
text-decoration: none;
padding: 10px;
text-align: center;
box-sizing: border-box;
border-radius: 10px;
background: #42c99d;
/* &.active{
  color: white;
} */
&:hover{
  color: white;
  background-color: rgb(40,176,255);
  transition: 150ms;
}
`

const Background = styled.div`
background-image: url(${backgroundImage});
position: relative;
width: 100%;
height: 40rem;
background-repeat: no-repeat;
background-size: 60%;
background-position: center;
`

export default Homepage;
