                       </span>

                       <span style={{ display:"flex"}}>
                       <p style={{ "color":"white", marginRight: "5px"}}> Due Date: </p>
                         <EasyEdit
                           type={Types.DATE}
                           value={task.dueDate}
                           onSave={(value) => { setFormData({dueDate: value, taskID: task.taskID,
                            nbrDeliverables: task.nbrDeliverables, details: task.details, comments: task.comments})}}
                           saveButtonLabel={<FontAwesomeIcon icon={faCheck} />}
                           cancelButtonLabel={
                             <FontAwesomeIcon icon={faTimes} />
                           } />
                        </span>

                       <span style={{ display:"flex"}} >
                       <p style={{ "color":"white", marginRight: "5px"}}> Number of Deliverables: </p>
                         <EasyEdit
                           type={Types.TEXT}
                           value={task.nbrDeliverables}
                           onSave={(value) => {
                             setFormData({nbrDeliverables: value, taskID: task.taskID,
                              dueDate: task.dueDate, details: task.details, comments: task.comments})}}
                           saveButtonLabel={<FontAwesomeIcon icon={faCheck} />}
                           cancelButtonLabel={
                             <FontAwesomeIcon icon={faTimes} />
                           } />
                        </span>

                       <span style={{ display:"flex"}} >
                       <p style={{ "color":"white", marginRight: "5px"}}> Task Details: </p>
                         <EasyEdit
                           type={Types.TEXT}
                           value={task.details}
                           onSave={(value) => {
                            setFormData({details: value, taskID: task.taskID, nbrDeliverables: task.nbrDeliverables,
                             dueDate: task.dueDate, comments: task.comments})}}
                           saveButtonLabel={<FontAwesomeIcon icon={faCheck} />}
                           cancelButtonLabel={
                             <FontAwesomeIcon icon={faTimes} />
                           }/>
                       </span>

                       <span style={{ display:"flex"}} >
                       <p style={{ "color":"white", marginRight: "5px"}}> Comments: </p>
                         <EasyEdit
                           type={Types.TEXT}
                           value={task.comments !== "" ? task.comments : "Click to edit" }
                           onSave={(value) => {
                            setFormData({comments: value, taskID: task.taskID, nbrDeliverables: task.nbrDeliverables,
                             dueDate: task.dueDate, details: task.details})}}
                           saveButtonLabel={<FontAwesomeIcon icon={faCheck} />}
                           cancelButtonLabel={
                             <FontAwesomeIcon icon={faTimes} />
                           } />
                        </span>

                        <div style={{display:"flex"}}>
                        <Button onClick={handleUpdate}>
                         Update Task
                        </Button>

                        <Button2 onClick={handleDelete}>
                         Delete Task
                        </Button2>
                      
                        <label style={ {display: "flex", alignItems:"center", position:"relative", top:"10%"}}>
                          <input type="radio" checked={checkedValue} onChange={handleChange} onClick={handleChange}/>
                          <span style={{
                            fontSize: "11px", overflowWrap:"break-word"}}>
                              <p  style={{width: "70%"}} >Check this to delete task</p></span>
                          </label>
                        
                        </div>  
        {/* responseObj.status !== 201 ?
         showMessage && <>
              <h3 style={{"marginTop": "10px","color":"#D80026",
             "fontSize":"15px", "textAlign":"center"}} >{responseObj.message}</h3>
            </>
         :
         (showMessage===false) &&  <>
            <h3 style={{"marginTop": "10px","color":"#D80026",
            "fontSize":"15px", "textAlign":"center"}} >{responseObj.message}</h3>
            {/* <p style={{"marginTop": "10px","color":"#D80026",
            "fontSize":"15px", "textAlign":"center"}}>
                Go to <b>My Projects</b> section to see your created Projects!</p> </>*/}
        
  </TaskWrapper>
  )}))  : (<SWrapper>
                    <Spinner/>
                    <p>Loading...</p>
                  </SWrapper>)
               }

              </ListWrapper>
            </HomeWrapper>
          ) : (
            //if page refreshes we use this message to indicate admin is logged out
            <div
              style={{
                width: "600px",
                position: "absolute",
                top: "50%",
                left: "30%",
              }}
            >
              <Title
                style={{
                  textAlign: "center",
                  borderRadius: "10px",
                  padding: "10px",
                  background: "red",
                }}
              >
                Admin signed out, go back to login page!
                <button
                  style={{
                    border: "none",
                    padding: "7px",
                    borderRadius: "10px",
                    background: "#28B0FF",
                    color: "white",
                  }}
                  onClick={() => navigate("/adminLogin")}
                >
                  <b>Go to Login</b>
                </button>
              </Title>
            </div>
          )
          }
        </Wrapper>
      </>
    );
    }

