import React, {useState} from "react";
import backgroundImage from "../assets/background2.png"
import styled from "styled-components";

const UserHomepage = () =>{
  // load data into useState
  const [uploadData, setUploadData]= useState();
  
  const filesUploadHandler= (e)=>{
      setUploadData(e.target.files[0])
      }
  
  const onSumbitHandler=(e) =>{
      e.preventDefault()
      // handle file data before sending it in the format that multer can read 
      const data = new FormData()
      data.append("files", uploadData)

      fetch("/uploads", {
          method: "POST",
          body: data,
      })
          .then((res)=>{
              console.log("Sucessful Upload")
              window.alert("Upload completed")
          })
          .catch((err)=>{
              console.log(err.message)
          })
  }    
    return (
        <>
            <Background/>
            <Title>Welcome User</Title>
            <Wrapper>
                <FormWrapper onSubmit={onSumbitHandler}>
                    <input 
                    type={"file"} 
                    onChange={filesUploadHandler}
                    style={{marginLeft:"2%"}}
                    />
                    <SubmitButton type="submit">Submit</SubmitButton>
                </FormWrapper>
            </Wrapper> 
        </>
      );
    }

    const Title = styled.h1`
    margin-top: 2%;
    margin-left: 3%;
    color: white;
    z-index: 1;
    `
    
const FormWrapper = styled.form`
display: flex;
justify-content: center;
align-items: center;
justify-content: space-between;
align-items: baseline;
background: #DDEDEC;
width: 100%;
height: 100%;
`
 
const Wrapper=styled.div`
    position: fixed;
    width: 100%;
    bottom:0; 
  `
    const Background = styled.div`
    background-image: url(${backgroundImage});
    position: fixed; 
    min-width: 100%;
    min-height: 100%;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    `

const SubmitButton = styled.button`
  color: white;
  font-weight: bold;
  padding: 12px 35px 12px 35px;
  margin-bottom: 0.2%;
  margin-top: 0.2%;
  margin-right: 1%;
  text-align: center;
  border: none;
  border-radius: 10px;
  background: blue;
  cursor: pointer;
  `
    export default UserHomepage;
