import React, { useState, useContext } from "react";
import backgroundImage from "../assets/background2.png";
import styled from "styled-components";
import { DataContext } from "./DataContext";
import Select from "react-select";

const UserHomepage = () => {
  // 1 ---------------------------- Find AssignedTask------------------------------------------
  const { currentLoggedUser, tasks } = useContext(DataContext);

  const findAssignedTasks = tasks.filter((task) =>
    task.assignedUsers.find(
      (el) =>
        currentLoggedUser.firstName === el.firstName &&
        currentLoggedUser.lastName === el.lastName
    )
  );

  console.log("findAssignedTasks", findAssignedTasks);

  // 2 - ---------------------- Uploading Files------------------------------------------------
  // 2.1 Load files into useState
  const [uploadData, setUploadData] = useState();

  const filesUploadHandler = (e) => {
    setUploadData(e.target.files[0]);
  };

  const onSumbitHandler = (e) => {
    e.preventDefault();
    // handle file data before sending it in the format that multer can read
    const data = new FormData();
    data.append("files", uploadData);

    fetch("/uploads", {
      method: "POST",
      body: data,
    })
      .then((res) => {
        window.alert("Upload completed");
      })
      .catch((err) => {
        console.log(err.message);
      });
  };

  const userDisp = findAssignedTasks[0];
  // !!!! need to do a map  for multiple task!!!!!!

  // Drop Down Progress Menu

  const options = [
    { value: "status", label: "Work in progress" },
    { value: "status", label: "Submitted for review" },
    { value: "status", label: "Started" },
  ];

  const [progress, setProgress] = useState(userDisp.status);
  const progressUpdate = (e) => {
    setProgress(e.label);
  };

  console.log("progress", progress);

  const formData = {
    taskID: userDisp.taskID,
    taskName: userDisp.taskName,
    dueDate: userDisp.dueDate,
    details: userDisp.details,
    status: progress,
    assignedUsers: userDisp.assignedUsers,
  };

  const updateTaskStat = () => {
    const settings = {
      method: "PUT",
      body: JSON.stringify(formData),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    fetch("/tasks", settings)
      .then((res) => res.json())
      .then((response) => {
        console.log(response);
        const { status, message } = response; //deconstructing response's status to know if succefull or not

        // if (status === 201) {
        //   setStatus("idle"); //set the status to idle meaning POST was succesfull
        //   setResMessage(message);
        // } else {
        //   setStatus("loading"); //set the status to loading meaning POST was not successfull
        //   setResMessage(message); // setting the received message from server into our response message state
        // }
      })
      .catch((error) => console.log("ERROR: ", error));
  };

  // const postUpdate = async (e) => {
  //   try {
  //     await setProgress({ ...formData, status: e.label });
  //     await updateTaskStat();
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };

  return (
    <>
      <Background />
      <Title>
        Welcome {currentLoggedUser.firstName + " " + currentLoggedUser.lastName}
      </Title>

      <DetailWrapper>
        <ProjectDetails>
          <h3 style={{ marginBottom: "1%" }}>
            Project Name: {" " + userDisp.projectName}
          </h3>
          <div style={{ marginLeft: "15px" }}>
            <h4 style={{ marginBottom: "1%" }}>
              Assigned task : {" " + userDisp.taskName}
            </h4>
            <div>Task details :{" " + userDisp.details}</div>
            <div>Due Date: {" " + userDisp.dueDate}</div>
            <div>Comments : {" " + userDisp.comments}</div>
          </div>
        </ProjectDetails>
        <Status>
          <div>Status:{" " + progress}</div>
          <div style={{ maxWidth: "300px" }}>
            <Select options={options} onChange={progressUpdate} />
          </div>
          <button onClick={updateTaskStat}>Update Status</button>
        </Status>
      </DetailWrapper>

      <Wrapper>
        <FormWrapper onSubmit={onSumbitHandler}>
          <input
            type={"file"}
            onChange={filesUploadHandler}
            style={{ marginLeft: "2%" }}
          />
          <SubmitButton type="submit">Submit</SubmitButton>
        </FormWrapper>
      </Wrapper>
    </>
  );
};

/* <a
        href="http://localhost:4000/Uploads/1652833066781%20IMG_2527.jpeg"
        download="Download1"
      >
        Download
      </a>
      <img src="http://localhost:4000/Uploads/1652833066781%20IMG_2527.jpeg" /> */

const Title = styled.h1`
  margin-top: 2%;
  margin-left: 3%;
  color: white;
`;

const FormWrapper = styled.form`
  display: flex;
  justify-content: center;
  align-items: center;
  justify-content: space-between;
  align-items: baseline;
  background: #ddedec;
  width: 100%;
  height: 100%;
`;

const Wrapper = styled.div`
  position: fixed;
  width: 100%;
  bottom: 0;
`;
const Background = styled.div`
  background-image: url(${backgroundImage});
  position: fixed;
  min-width: 100%;
  min-height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  z-index: -1;
`;

const SubmitButton = styled.button`
  color: white;
  font-weight: bold;
  padding: 12px 35px 12px 35px;
  margin-bottom: 0.2%;
  margin-top: 0.2%;
  margin-right: 1%;
  text-align: center;
  border: none;
  border-radius: 10px;
  background: blue;
  cursor: pointer;
`;

const DetailWrapper = styled.div`
  align-content: flex-start;
  margin-top: 2%;
  margin-left: 3%;
  color: black;
  background: #42c99d;
  opacity: 0.7;
  margin-right: 5%;
  border-radius: 5px;
  max-height: 20vh;
`;

const ProjectDetails = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  //align-items: center;
`;

const Status = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-content: flex-end;
  background: grey;
  opacity: 1;
`;

export default UserHomepage;
