<img src="http://localhost:4000/Uploads/1652833066781%20IMG_2527.jpeg" /> */
}

const Title = styled.h1`
  margin-top: 2%;
  margin-left: 3%;
  color: white;
`;

const TaskWrapper = styled.div`
  width: 40vw;
  margin-left: 3%;
  margin-bottom: 20px;
  border-radius: 5px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  background: #42c99d;
  /* align-items:baseline; */
  font-weight: normal;
  h3 {
    border-bottom: 1px solid;
    margin-bottom: 5px;
  }
  p {
    color: white;
  }

  .tooltip {
    position: relative;
    display: inline-block;
  }

  .tooltip .tooltiptext {
    visibility: hidden;
    width: 120px;
    background-color: #5b9bd5;
    color: #fff;
    text-align: center;
    border-radius: 6px;
    padding: 5px 0;

    /* Position the tooltip */
    position: absolute;
    z-index: 1;
    top: -5px;
    left: 105%;
  }

  .tooltip:hover .tooltiptext {
    visibility: visible;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  font-size: 15px;
  border: none;
  background: #f16436;
  padding: 10px;
  width: 150px;
  margin-top: 15px;
  color: white;
  &:hover {
    color: white;
    background-color: rgb(40, 176, 255);
    transition: 150ms;
  }
`;

const FormWrapper = styled.form`
  display: flex;
  justify-content: center;
  align-items: center;
  justify-content: space-between;
  align-items: baseline;
  background: #ddedec;
  width: 100%;
  height: 100%;
`;

const Wrapper = styled.div`
  position: fixed;
  width: 100%;
  bottom: 0;
`;
const Background = styled.div`
  background-image: url(${backgroundImage});
  position: fixed;
  min-width: 100%;
  min-height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  z-index: -1;
`;

const SubmitButton = styled.button`
  color: white;
  font-weight: bold;
  padding: 12px 35px 12px 35px;
  margin-bottom: 0.2%;
  margin-top: 0.2%;
  margin-right: 1%;
  text-align: center;
  border: none;
  border-radius: 10px;
  background: blue;
  cursor: pointer;
`;

const DetailWrapper = styled.div`
  align-content: flex-start;
  max-width: 47%;
  margin-top: 2%;
  margin-left: 3%;
  color: black;
  background: #42c99d;
  opacity: 0.7;
  margin-right: 5%;
  border-radius: 5px;
  max-height: 20vh;
`;

const StatusBar = styled.div`
  max-width: 250px;
  margin-left: 51%;
  margin-top: -8%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const StatusDisp = styled.div`
  text-align: center;
  background: greenyellow;
  border-radius: 2px;
  margin-bottom: 10px;
`;

const StatusBtn = styled(SubmitButton)`
  text-align: center;
  max-height: 3vh;
`;
export default UserHomepage;
