}

const Title = styled.h1`
  margin-top: 2%;
  margin-left: 3%;
  color: white;
`;

const HomeWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 900px;
  margin: 50px;
  background: rgba(221, 237, 236, 1);
  border-radius: 5px;
`;

const ListWrapper = styled.ul`
  list-style-type: none;
  display: flex;
  flex-direction: column;
  font-weight: bold;
  padding: 20px 0px 20px 20px;
  width: 17rem;
`;

const TaskWrapper = styled.div`
  width: 40vw;
  margin-left: 3%;
  margin-bottom: 20px;
  border-radius: 5px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  background: #42c99d;
  /* align-items:baseline; */
  font-weight: normal;
  h3 {
    border-bottom: 1px solid;
    margin-bottom: 5px;
  }
  p {
    color: white;
  }

  .tooltip {
    position: relative;
    display: inline-block;
  }

  .tooltip .tooltiptext {
    visibility: hidden;
    width: 120px;
    background-color: #5b9bd5;
    color: #fff;
    text-align: center;
    border-radius: 6px;
    padding: 5px 0;

    /* Position the tooltip */
    position: absolute;
    z-index: 1;
    top: -5px;
    left: 105%;
  }

  .tooltip:hover .tooltiptext {
    visibility: visible;
  }
`;

const Button = styled.button`
  border-radius: 5px;
  font-size: 15px;
  border: none;
  background: #f16436;
  padding: 10px;
  width: 150px;
  margin-top: 15px;
  color: white;
  &:hover {
    color: white;
    background-color: rgb(40, 176, 255);
    transition: 150ms;
  }
  &:disabled {
    cursor: not-allowed;
    opacity: 0.7;
  }
`;

const FormWrapper = styled.form`
  display: flex;
  justify-content: center;
  align-items: center;
  justify-content: space-between;
  align-items: baseline;
  background: #ddedec;
  width: 100%;
  height: 100%;
`;

const Wrapper = styled.div`
  position: fixed;
  width: 100%;
  bottom: 0;
`;
const Background = styled.div`
  background-image: url(${backgroundImage});
  position: absolute;
  min-width: 100%;
  min-height: 150%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  z-index: -1;
`;

const SubmitButton = styled.button`
  color: white;
  font-weight: bold;
  padding: 12px 35px 12px 35px;
  margin-bottom: 0.2%;
  margin-top: 0.2%;
  margin-right: 1%;
  text-align: center;
  border: none;
  border-radius: 10px;
  background: blue;
  cursor: pointer;
`;

export default UserHomepage;
